import React, { Component } from 'react';
import { HashRouter as Router, Route, Link } from 'react-router-dom';
import axios from './axios';

export default class App extends Component {
    constructor(props){
        super(props);
        this.state={email:'',password:''};
        this.handleChange=this.handleChange.bind(this);
        this.find=this.find.bind(this);


    }
    handleChange(event){

        if (event.target.name === 'email'){

            this.setState({ 'email': event.target.value });
        }
        if (event.target.name === 'password') {
            this.setState({'password':event.target.value});
        }
    }
        find(event){
        event.preventDefault();
        if (this.state.find==='') {
            alert('required');
        }else{
            axios.post('http://10.90.90.110:3000/find',{email:this.state.find}).then(res=>{
                  if(res.data.status === 'success'){

                this.setState({find:res.data.reguser});

            }
            console.log(this.state.find);
            })
        }
    }


  render() {
    return (

<div class="col-sm-6 col-sm-offset-3">

    <h1>Admin Login</h1>
    <form action="/login" method="post">
        <div class="form-group">
            <label>Email</label>
            <input type="text"  class="form-control" name="email" onChnage={this.handleChnage}/>
        </div>
        <div class="form-group">
            <label>Password</label>
            <input type="password" class="form-control" name="password" onChnage={this.handleChnage}/>
        </div>
        <button type="submit" class="btn btn-warning btn-lg" onClick={this.find}>Login</button>
    </form>
    <hr/>
    <p>Or go <a href="/">home</a>.</p>

</div>
  );
 }
}
