import React,{Component} from 'react';
import { HashRouter as Router, Route, Link } from 'react-router-dom';
import profile from './profile';
import axios from 'axios';

class Login extends Component {
	constructor(props){
  super(props);
  this.state={email:'',password:''};
  this.handleChange=this.handleChange.bind(this);
  this.handleSubmit=this.handleSubmit.bind(this);
	}	
  handleChange(event){

		if (event.target.name=== 'email'){

			this.setState({ 'email': event.target.value });
        }

		if (event.target.name === 'password'){

			this.setState({ 'password': event.target.value });
        }
    }
        handleSubmit(event){
        	var emailpattern=/^\w+([.-]?\w+)*@([\w]+)[.]([\w]){3}$/;
        	if (emailpattern.test(this.state.email)) {
        		var userDetails={
				email:this.state.email,
				password:this.state.password
			}
			console.log(userDetails);
            axios.post('http://10.90.90.110:3000/stories',userDetails).then(res=>{
				console.log(res.userDetails);

			})
        }else{
            	alert('enter valid details');

			}

        }

    render() {
   	 return (
 <div class="container">
<div class="col-sm-6 col-sm-offset-3">

    <h1><span class="fa fa-sign-in"></span>Login</h1>


    <form action="/login" method="post" onSubmit={this.handleSubmit}>
        <div class="form-group">
            <label>Email</label>
            <input type="text" class="form-control" name="email"  onChange={this.handleChange}>
            </input>
        </div>
        <div class="form-group">
            <label>Password</label>
            <input type="password" class="form-control" name="password"  onChange={this.handleChange}>
            </input>
        </div>
      <Router><Link to ='/profile' >login</Link></Router>
    </form>
    <hr></hr>
     <p><a href="/">signup</a></p>
    <p>or go <a href="/">home</a></p>
    <Route path="/profile" component={profile}></Route>

</div>
</div>
   	 	 );
  }
}

export default Login;
