import { HashRouter as Router, Route, Link } from 'react-router-dom';
import signup from './components/signup';
import axios from 'axios';

class Login extends Component {
	constructor(props){
  super(props);
  this.state={email:'',password:''};
  this.handleChange=this.handleChange.bind(this);
  this.handleSubmit=this.handleSubmit.bind(this);
	}	
  handleChange(event){

		if (event.target.name=== 'email'){

			this.setState({ 'email': event.target.value });
        }

		if (event.target.name === 'password'){

			this.setState({ 'password': event.target.value });
        }
    }



  handleSubmit(event){
        	var emailpattern=/^\w+([.-]?\w+)*@([\w]+)[.]([\w]){3}$/;
        	if (emailpattern.test(this.state.email)) {
        		var userDetails={
				email:this.state.email,

			}


      console.log(userDetails);
            axios.post('http://10.90.90.110:3000/story',userDetails).then(res=>{
        console.log(res.userDetails);
        if (res.data.status === 'failed') {
          alert('already registerd');
        }else{
          alert('success');
        }
      })
            }
    else{
              alert('enter valid details');

      }
    }



        check(event){
          if (this.state.find.email == 'manu@gmail.com') {
            alert('you are admin');
          }else{
            alert('you are user');
          }
        }


    render() {
   	 return (
 <div class="container">
<div class="col-sm-6 col-sm-offset-3">

    <h1><span class="fa fa-sign-in"></span>Login</h1>


    <form action="/login" method="post" onSubmit={this.handleSubmit}>
        <div class="form-group">
            <label>Email</label>
            <input  class="form-control" name="email"  onChange={this.handleChange}>
            </input>
        </div>
        <div class="form-group">
            <label>Password</label>
            <input  class="form-control" name="password"  onChange={this.handleChange}/>

        </div>
      <button type='submit' onClick={this.check}><b>check</b></button>
    </form>
    <hr></hr>
     <Router><Link to ="/">signup</Link></Router>
    <p>or go <a href="/">home</a></p>
    <Route path='/signup' component={signup}></Route>


</div>
</div>
   	 	 );
  }
}

export default Login;
